tinymce.PluginManager.add("imager", function(e, t) {
    e.addButton("imager", {
        icon: "image",
        onclick: function() {
            open_imager({multiple: false, tinymce: 'true'});
            $('#imager').on('hidden.bs.modal', function () {
                if(document.querySelector('#imager').getAttribute('data-last-select')){
                    e.insertContent('<img src="'+base_url + "/uploads/" + document.querySelector('#imager').getAttribute('data-last-select')+'" alt="'+document.querySelector('#imager').getAttribute('data-last-select')+'" />')
                    document.querySelector('#imager').removeAttribute('data-last-select')
                }
                var src = document.querySelector('#imager iframe').getAttribute('src');
                    src = src.split("?")[0]+"?"
                document.querySelector('#imager iframe').setAttribute('src', src)
              })

        }
    })
});
